mapper.select:
    select * from trade WHERE symbol_id = #{symbol.symbolId}

    	AND datetime >= #{filter.startTime}


    	AND datetime < #{filter.endTime}


    	ORDER BY datetime ${filter.order}




	    	Limit #{filter.limit}


	    	Limit 10000

	(SELECT * FROM trade WHERE symbol_id = #{symbol.symbolId}
	  	AND datetime < #{filter.startTime}
  	ORDER BY datetime DESC
  	Limit #{filter.limit})
  UNION 
	(SELECT * FROM trade WHERE symbol_id = #{symbol.symbolId}
	  	AND datetime >= #{filter.startTime}
  	ORDER BY datetime ASC
  	Limit #{filter.limit})
  ORDER BY datetime ASC
